$(document).ready(function(){function e(){return window.pageYOffset||document.documentElement.scrollTop}$(window).scroll(function(){e()>=300?$(".header").addClass("shrink"):$(".header").removeClass("shrink")})});var headerHeight=$("header").height();$(".flex-nav .flex-item-list").find("a").bind("click",function(e){e.preventDefault();var t=$(this).attr("href"),n=$(t).offset().top-headerHeight;$("html, body").animate({scrollTop:n},900,function(){window.location.hash=""+t,$("html, body").animate({scrollTop:n},0)})});